            this.removeWindowComponentDashboard(windowRef);
            sub.unsubscribe();
        });
        let newInjector = createEnvironmentInjector([
            {
                provide: 'GDWindowToken',
                useValue: config
            }
        ], this.environmentInjector);
        let windowRef = createComponent(GDWindowComponent, { environmentInjector: newInjector });
        this.appRef.attachView(windowRef.hostView);
        let domWindow = windowRef.hostView.rootNodes[0];
        dashboard.el.nativeElement.children[0].children[0].children[0].appendChild(domWindow);
        windowRef.instance.setConfig(config.windowConfig);
        windowRef.instance.State = 'open';
        windowRef.instance.onClose.subscribe(() => {
            // I have inside _windowRef            
