            overlayRef.instance.Opacity = config.windowConfig.overlayOpacity;
            overlayRef.instance.Background = config.windowConfig.overlayBackground;
            overlayRef.instance.closeChildOnClick = config.windowConfig.overlayClickClose;
            overlayRef.instance.closeChildOnMove = config.windowConfig.overlayMoveClose;
